Scripts.OnClick:
if( CTA_PlayerListOffset > 0 ) then
	CTA_PlayerListOffset = CTA_PlayerListOffset - CTA_MAX_BLACKLIST_ITEMS;
	CTA_UpdateGreyListItems();
end

if( getn(CTA_BlackList)  > CTA_PlayerListOffset + CTA_MAX_BLACKLIST_ITEMS ) then
	CTA_PlayerListOffset = CTA_PlayerListOffset + CTA_MAX_BLACKLIST_ITEMS;
	CTA_UpdateGreyListItems();
end

