Scripts.OnMouseUp:
if (arg1 == "MiddleButton") then
	this.moving = nil;
else
	DUF_UnitFrame_Nudge_Up(arg1);
end

if (arg1 == "MiddleButton") then
	this.moving = nil;
else
	DUF_UnitFrame_Nudge_Down(arg1);
end

if (arg1 == "MiddleButton") then
	this.moving = nil;
else
	DUF_UnitFrame_Nudge_Left(arg1);
end

if (arg1 == "MiddleButton") then
	this.moving = nil;
else
	DUF_UnitFrame_Nudge_Right(arg1);
end

