Scripts.OnClick:
GathererUI_HelpFrame_Update();
if ( GathererUI_HelpFrame:IsShown() ) then
	GathererUI_HelpFrame:Hide();
else
	GathererUI_HelpFrame:Show();
end

Scripts.OnShow:
if ( Gather_Player and GatherConfig.users[Gather_Player].minSetHerbSkill ) then
	this:SetNumber(GatherConfig.users[Gather_Player].minSetHerbSkill);
else
	this:SetNumber(0);
end

if ( Gather_Player and GatherConfig.users[Gather_Player].minSetOreSkill ) then
	this:SetNumber(GatherConfig.users[Gather_Player].minSetOreSkill);
else
	this:SetNumber(0);
end

Scripts.OnClick:
						if (this:GetChecked()) then
							GatherConfig.users[Gather_Player].filterRecording[1] = 1;
						else
   							GatherConfig.users[Gather_Player].filterRecording[1] = nil;
							GathererUI_CheckHerbRecord:SetChecked(GatherConfig.users[Gather_Player].filterRecording[1]);
						end

						if (this:GetChecked()) then
							GatherConfig.users[Gather_Player].filterRecording[2] = 1;
						else
   							GatherConfig.users[Gather_Player].filterRecording[2] = nil;
							GathererUI_CheckHerbRecord:SetChecked(GatherConfig.users[Gather_Player].filterRecording[2]);
						end

						if (this:GetChecked()) then
							GatherConfig.users[Gather_Player].filterRecording[0] = 1;
						else
   							GatherConfig.users[Gather_Player].filterRecording[0] = nil;
							GathererUI_CheckHerbRecord:SetChecked(GatherConfig.users[Gather_Player].filterRecording[0]);
						end

						if (this:GetChecked()) then
							GatherConfig.showWorldMapFilters = 1;
							GathererWD_DropDownFilters:Show();
						else
   							GatherConfig.showWorldMapFilters = 0;
							GathererWD_DropDownFilters:Hide();
						end

if (this:GetChecked()) then
	GatherConfig.mapMinder = true;
else
	GatherConfig.mapMinder = false;
end

if (this:GetChecked()) then
	GatherConfig.NoIconOnMinDist = 1;
else
	GatherConfig.NoIconOnMinDist = 0;
end
Gatherer_OnUpdate(0, true);

if (this:GetChecked()) then
	GatherConfig.rareOre = 1;
else
	GatherConfig.rareOre = 0;
end

if (this:GetChecked()) then
	GatherConfig.HideMiniNotes = 1;
else
	GatherConfig.HideMiniNotes = 0;
end

if (this:GetChecked()) then
	GatherConfig.ToggleWorldNotes = 1;
else
	GatherConfig.ToggleWorldNotes = 0;
end

Scripts.OnShow:
if ( Gather_Player and GatherConfig.IconSize ) then
	this:SetNumber(GatherConfig.IconSize);
else
	this:SetNumber(12);
end

if ( Gather_Player and GatherConfig.IconAlpha ) then
	this:SetNumber(GatherConfig.IconAlpha);
else
	this:SetNumber(80);
end

Scripts.OnClick:
if (this:GetChecked()) then
	GatherConfig.ShowOnMouse = 1;
else
	GatherConfig.ShowOnMouse = 0;
	GatherConfig.ShowOnClick = 1;
	GathererUI_CheckShowOnClick:SetChecked(GatherConfig.ShowOnClick);
end

if (this:GetChecked()) then
	GatherConfig.HideOnMouse = 1;
else
	GatherConfig.HideOnMouse = 0;
end

if (this:GetChecked()) then
	GatherConfig.HideOnClick = 1;
else
	GatherConfig.HideOnClick = 0;
end

if (this:GetChecked()) then
	GatherConfig.HideOnButton = 1;
else
	GatherConfig.HideOnButton = 0;
end

if (this:GetChecked()) then
	GatherConfig.ShowOnClick = 1;
else
	GatherConfig.ShowOnClick = 0;
	GatherConfig.ShowOnMouse = 1;
	GathererUI_CheckShowOnMouse:SetChecked(GatherConfig.ShowOnMouse);
end

if (this:GetChecked()) then
	GatherConfig.HideIcon = 1;
	GathererUI_IconFrame:Hide();
else
	GatherConfig.HideIcon = 0;
	GathererUI_IconFrame:Show();
end

if (this:GetChecked()) then
	gathererFixItems = 1;
else
	gathererFixItems = 0;
end

Scripts.OnShow:
if ( GatherConfig.useMainmap and GatherConfig.showWorldMapFilters and GatherConfig.showWorldMapFilters == 1 ) 
then
	UIDropDownMenu_ClearAll(this);
	UIDropDownMenu_Initialize(this, Gatherer_WorldMapFilter_Load);
	this:SetScale(this:GetParent():GetEffectiveScale() - 0.2);
else
	this:Hide();
end
