Scripts.OnHide:
if ( this.isMoving ) then
this:StopMovingOrSizing();
this.isMoving = false;
end

Scripts.OnMouseDown:
if (arg1 == "LeftButton") then
this:StartMoving();
this.isMoving = true;
end

Scripts.OnMouseUp:
if ( this.isMoving ) then
this:StopMovingOrSizing();
this.isMoving = false;
end
