Scripts.OnEnter:
if ( this.tooltipText ) then
	GameTooltip:SetOwner(this, "ANCHOR_RIGHT");
	GameTooltip:SetText(this.tooltipText, nil, nil, nil, nil, 1);
end

Scripts.OnLeave:
if (this.flashFrame) then local array if (table.getn(this.flashFrame) == 0) then array = {this.flashFrame} else array = this.flashFrame end for i,f in pairs(array) do if (f and UIFrameIsFlashing(f)) then UIFrameFlashStop(f) UIFrameFlashUpdate(1)
local show = 1 if (this.flashShow) then show = XPerlConfig[this.flashShow] end if (show == 1) then f:Show() else f:Hide() end end end
if (this.flashEnd) then this.flashEnd() end end GameTooltip:Hide()
